import * as React from 'react';
import { View, ScrollView } from 'react-native';
import { SafeAreaView } from "react-native-safe-area-context";

// Import helper code
import Settings from '../constants/Settings';
import { GetProductById, RoiDeletePerson, GetProducts } from '../utils/Api';
import { PopupOk, PopupOkCancel } from "../utils/Popup";

// Import styling and components
import { TextParagraph, TextH1, TextH2, TextLabel } from "../components/StyledText";
import Styles from "../styles/MainStyle";
import { MyButton } from '../components/MyButton';

import { showMessage } from "react-native-flash-message";
import NetInfo from "@react-native-community/netinfo";

export default function ViewMenuItemScreen(props) {

  //Set up defaul person object

  const MenuItemTemplate = { 
    id: 0,
    name: "",
    description : "",
    price:0 ,
    category: "",
    ingredients: "",
    dietary: "",



  }

  // State - data for this component

  // Store person in state
  const [MenuItem, setMenuItem] = React.useState(MenuItemTemplate)

  // Set "effect" to retrieve and store data - only run on mount/unmount (loaded/unloaded)
  // "effectful" code is something that triggers a UI re-render
  React.useEffect(refreshMenuItem, [])

  // Refresh the person data - call the API
  function refreshMenuItem() {

    //Get person id from route
    const MenuItemId = props.route.params.id

    // Get data from the API
    GetProductById(MenuItemId)
      // Success
      .then(data => {
        // Store results in state variable
        if(data) setMenuItem(data)
      })
      // Error
      .catch(error => {

        // Display error
        PopupOk("API Error", "Could not get menu item  from the server")

        //This is optional
        props.navigation.navigate("ViewMenu")

      })

  }

  function showAddPerson() {

    console.log("show add person...")

  }

  function showEditPerson() {

    props.navigation.navigate("EditPerson", {id: person.id})

  }

  async function deletePerson() {



    //Display flash message when there is a connection issue
    displayDangerConnectionMessage()

    //cancel if no internet connection 
    if(!(await NetInfo.fetch()).isConnected)return

    PopupOkCancel(
      "Delete Person?",
      `Are you sure you want to delete ${person.name}`,
      () => {
        RoiDeletePerson(person.id)
        .then(date => {
            PopupOk("Person deleted", `${person.name} has been deleted.`)
            props.navigation.navigate("ViewPeople")
        }).catch(error => {
          PopupOk("Error", error)
        })
      }
    )
  }


  // Main output of the screen component
  return (
    <SafeAreaView style={Styles.safeAreaView}>

      <ScrollView style={Styles.container} contentContainerStyle={Styles.contentContainer}>

        <TextH1 style={{marginTop:0}}>menuItem: {MenuItem.name}</TextH1>

        <View style={Styles.form}>

          <View style={Styles.fieldSet}>
              <TextParagraph style={Styles.legend}>Details</TextParagraph>
              <View style={Styles.formRow}>
                <TextLabel>Name:</TextLabel>
                <TextParagraph>{MenuItem.name}</TextParagraph>
              </View>
              <View style={Styles.formRow}>
                <TextLabel>Description:</TextLabel>
                <TextParagraph>{MenuItem.description}</TextParagraph>
              </View>
              <View style={Styles.formRow}>
                <TextLabel>Price:</TextLabel>
                <TextParagraph>{MenuItem.price}</TextParagraph>
              </View>
              <View style={Styles.formRow}>
                <TextLabel>Category:</TextLabel>
                <TextParagraph>{MenuItem.category}</TextParagraph>
              </View>
              <View style={Styles.formRow}>
                <TextLabel>Ingredients:</TextLabel>
                <TextParagraph>{MenuItem.ingredients}</TextParagraph>
              </View>
              <View style={Styles.formRow}>
                <TextLabel>Dietary:</TextLabel>
                <TextParagraph>{MenuItem.dietary}</TextParagraph>
              </View>
          </View>



        </View>

        {/* Buttons */}
        {/* <View style={[Styles.personButtonContainer,{borderBottomWidth:0}]} >
          <MyButton
            text="Edit"
            type="major"      // default*|major|minor
            size="medium"      // small|medium*|large
            onPress={showEditPerson}
          />
         <MyButton
            text="Delete"
            type="default"      // default*|major|minor
            size="medium"      // small|medium*|large
            onPress={deletePerson}
          /> 
        </View> */}

      </ScrollView>
    </SafeAreaView>
  );
}







function displayDangerConnectionMessage() {
  //Get Network connection status
  NetInfo.fetch().then(status => {
    //check if not connected to the internet
    if (!status.isConnected) {
      // if(true){
      //Display the flash message
      showMessage({

        message: "No internet connection",
        description: "You can not delete a  person untill  you \nhave an active internet connection.",
        type: "danger",
        duration: 5000,
        floating: true,
        icon: "danger",
        autoHide: false,
      })
    }
  })
}
