import * as React from 'react';
import { Image, Text, View, ScrollView, Pressable, TextInput } from 'react-native';
import { SafeAreaView } from "react-native-safe-area-context";
// Import helper code
import Settings from '../constants/Settings';
// Import styling and components
import Styles from "../styles/MainStyle";
import { MyButton } from '../components/MyButton';
import { TextParagraph, TextH1, TextH2, TextLabel, TextH3  } from "../components/StyledText";
import { Login } from '../utils/Api';

export default function HomeScreen(props) {
  //State management
  const [isLogoColour, setIsLogoColour] = React.useState(true);

  const [username, setUsername] = React.useState("");
  const [password, setPassword] = React.useState("");
  
  const [error, setError] = React.useState(false);



  function toggleLogo() {
    setIsLogoColour(!isLogoColour)
  }

  function showHelp() {
    props.navigation.replace('Root', { screen: 'Help' });
  }



  function login() {
    
    Login({username,password})
    .then(data => {
        if(data==true)
        {
          props.navigation.replace('Root', { screen: 'Menu' });
        }
        else
        {
            setError(true)
        }
    }).catch(error => {
      
    })
    
  
  }




  return (
    <SafeAreaView style={Styles.safeAreaView}>
      <ScrollView style={Styles.container} contentContainerStyle={Styles.contentContainer}>
        {/* Logo */}
        <View style={Styles.homeLogoContainer}>
          <Pressable onPress={toggleLogo}>
            <Image
              //condotion
              source={
                isLogoColour
                  ? require("../assets/images/roi-logo.jpg")
                  : require("../assets/images/roi-logo-monochrome.jpg")

              }
              style={Styles.homeLogo}
            />
          </Pressable>
        </View>


        {/* Heading */}
        <View style={Styles.homeHeadingContainer}>
          <Text style={Styles.homeHeading}> Login to Bean Scene Restaurant </Text>
        </View>

        {/* <TextH1>This is the home screen</TextH1>

        <TextParagraph>Here is some sample text for the home screen.</TextParagraph>

        <TextParagraph>There is no place like 127.0.0.1</TextParagraph> */}
        {/* Buttons */}
        <View style={Styles.fieldSet}>
          <View style={Styles.formRow}>
            <TextLabel>Username:</TextLabel>
            <TextInput value={username} onChangeText={setUsername} style={Styles.textInput} />
          </View>

          <View style={Styles.formRow}>
            <TextLabel>Password:</TextLabel>
            <TextInput value={password} onChangeText={setPassword} style={Styles.textInput} />
          </View>

          <View style={Styles.homeButtonContainer}>
            <MyButton
              text="Login"
              type="major"      // default*|major|minor
              size="large"      // small|medium*|large
              onPress={login}
              buttonStyle={Styles.loginButton}
            />


            {/* <MyButton
            text="Help"
            type="defualt"      // default*|major|minor
            size="large"      // small|medium*|large
            onPress={showHelp}
            buttonStyle={Styles.homeButton}
          /> */}


          </View>

          {error && <TextH3 style={{color:'red'}}>Username or password is wrong</TextH3>}

        </View>
      </ScrollView>
    </SafeAreaView>
  );
}
