import * as React from 'react';
import { View, ScrollView, TextInput } from 'react-native';
import { SafeAreaView } from "react-native-safe-area-context";
import {Picker} from '@react-native-picker/picker';

import { showMessage } from "react-native-flash-message";
import NetInfo from "@react-native-community/netinfo";

// Import helper code
import { RoiCreatePerson, RoiGetDepartment } from '../utils/Api';
import { PopupOk, PopupOkCancel } from "../utils/Popup";

// Import styling and components
import { TextParagraph, TextH1, TextH2, TextLabel } from "../components/StyledText";
import Styles from "../styles/MainStyle";
import { MyButton } from '../components/MyButton';


// Import helper code
import Settings from '../constants/Settings';
import { RoiDeletePerson, RoiGetPeople } from '../utils/Api';

// Import styling and components

import { NetInfoCellularGeneration } from '@react-native-community/netinfo';


export default function OrderScreen(props) {


  // State - data for this component

  // Data array, default to empty array
  const [orderItems, setOrderItems] = React.useState([])

  // Set "effect" to retrieve and store data - only run on mount/unmount (loaded/unloaded)
  // "effectful" code is something that triggers a UI re-render
  React.useEffect(refreshPersonList, [])

  // Refresh the person list data - call the API
  function refreshPersonList() {

    console.log("refresh person list")

    // Get data from the API
    RoiGetPeople()
      // Success
      .then(data => {
        // Store results in state variable
        setOrderItems(data)
      })
      // Error
      .catch(error => {
        PopupOk("API Error", "Could not get people from the server")
      })

  }

  // function showAddPerson() {

  //   props.navigation.navigate("AddPerson")

  // }

  function showViewMenuItem(menuItem) {

    //Navigate to view person and pass person id as param
    props.navigation.navigate("ViewMenuItem", { id: menuItem.id })

  }

  function showOrderList(menuItem) {

    //Navigate to view person and pass person id as param
    props.navigation.navigate("Order", { id: menuItem.id })

  }



  //Delete Person
  //@param {Person} Person

  async function deletePerson(person) {


    //Display flash message when there is a connection issue
    displayDangerConnectionMessage()

    //cancel if no internet connection 
    if(!(await NetInfo.fetch()).isConnected)return

    PopupOkCancel(
      "Delete person?",
      `Are you sure you want to delete ${person.name}?`,
      () => {

        //console.log("OK...deleting person")
        RoiDeletePerson(person.id)
          .then(data => {

            PopupOk("Person deleted", ` ${person.name} has been deleted.`)
            refreshPersonList()


          })


          .catch(error => {

            PopupOk("Error", error)
          })

      },

      //cancel do nothing
      () => {

        //console.log("Cancel-no delete for you")
      }

    )
  }



  //Display flash message banner if offline
  function displayConnectionMessage() {
    //Get Network connection status
    NetInfo.fetch().then(status => {
      //check if not connected to the internet
      if (!status.isConnected) {
        // if(true){
        //Display the flash message
        showMessage({

          message: "No  internet connection",
          description: "You will only see cached data until you \nhave an active internet connection again.",
          type: "warning",
          duration: 5000,
          floating: true,
          icon: "warning",
          autoHide: true,
        })
      }
    })
  }


  // Display all people data
  function displayOrderItems() {
    //Display flash message when there's connection issue
    displayConnectionMessage()


    if (!people) return

    // Loop through each item and turn into appropriate output and then return the result
    return people.map(p => {

      // Create an output view for each item
      return (
        <View key={p.id} style={Styles.personListItem}>
          <View style={Styles.personListItemDetails}>
            <TextParagraph style={Styles.personListItemName}>{p.name}</TextParagraph>
            <TextParagraph style={Styles.personListItemText}>{p.department?.name ?? "---"}</TextParagraph>

            <TextParagraph style={Styles.personListItemText}>{p.phone}</TextParagraph>
          </View>
          <View style={Styles.personListItemButtons}>

            {/* <MyButton
              text="View Menu Item"
              type="major"      // default*|major|minor
              size="small"      // small|medium*|large
              //onPress={showAddPerson}
              buttonStyle={Styles.personListItemButton}
              textStyle={Styles.personListItemButtonText}
              onPress={() => showViewMenuItem(p)}
            /> */}




            <View style={Styles.formRow}>
            <MyButton
              text="+"
              type="default"      // default*|major|minor
              size="small"      // small|medium*|large
              //onPress={showAddPerson}
              buttonStyle={Styles.personListItemButton}
              textStyle={Styles.personListItemButtonText}
              onPress={() => showOrderList(p)}
            />
            <TextInput value={0} style={Styles.textInput}/>
            <MyButton
              text="-"
              type="default"      // default*|major|minor
              size="small"      // small|medium*|large
              //onPress={showAddPerson}
              buttonStyle={Styles.personListItemButton}
              textStyle={Styles.personListItemButtonText}
              onPress={() => showOrderList(p)}
            />
              </View>


            <MyButton
              text="Delete"
              type="minor"      // default*|major|minor
              size="small"      // small|medium*|large
              onPress={() => deletePerson(p)}
              buttonStyle={Styles.personListItemButton}
              textStyle={Styles.personListItemButtonText}
            />






          </View>
        </View>
      )

    })

  }


  // Main output of the screen component
  return (
    <SafeAreaView style={Styles.safeAreaView}>

      {/* <View style={Styles.personButtonContainer}>
        <MyButton
          text="+ Add new person"
          type="major"      // default*|major|minor
          size="small"      // small|medium*|large
          onPress={showAddPerson}
        />
        <MyButton
          text="Refresh"
          type="default"    // default*|major|minor
          size="small"      // small|medium*|large
          onPress={refreshPersonList}
        />
      </View> */}

      <ScrollView style={Styles.container} contentContainerStyle={Styles.contentContainer}>

        <TextH1 style={{ marginTop: 0 }}>Your order list</TextH1>

        <View style={Styles.personList}>
          {displayOrderItems()}
        </View>

      </ScrollView>
    </SafeAreaView>
  );
}
